/**
 * @fileoverview Notification area component.
 *
 * @author lildanzig@google.com (Jay Friedman)
 */

goog.provide('gc.Notification');
goog.provide('gc.Notification.Area');
goog.provide('gc.Notification.CssClass');
goog.provide('gc.Notification.DisplayTime');

goog.require('goog.Delay');
goog.require('goog.dom');
goog.require('goog.dom.classes');
goog.require('goog.events.EventTarget');
goog.require('goog.structs.Map');
goog.require('goog.style');


/**
 * Notification class. Controls a 'notification' element.
 *
 * @param {Element} element The element to use as the notification.
 * @constructor
 * @extends {goog.events.EventTarget}
 */
gc.Notification = function(element) {
  goog.events.EventTarget.call(this);

  /**
   * Element to use as the notification display.
   * @type {Element}
   * @private
   */
  this.display_ = element;

  var textElement = goog.dom.getElementsByTagNameAndClass(null,
      'gc-status-text', element);

  if (textElement.length) {
    textElement = textElement[0];
  } else {
    textElement = element;
  }

  /**
   * Element where the text will be placed for the notification.
   * @type {Element}
   * @private
   */
  this.textElement_ = textElement;
};
goog.inherits(gc.Notification, goog.events.EventTarget);

/**
 * Global instances for each notification element.
 * @type {goog.structs.Map.<gc.Notification>?}
 * @private
 */
gc.Notification.instances_ = new goog.structs.Map();

/**
 * Common notification areas.
 * @enum {string}
 */
gc.Notification.Area = {
  /** Area at absolute top of the site. */
  TOP: 'gc-view-loading',

  /** Area in center of page. */
  STATUS: 'gc-view-notification'
};

/**
 * Common notification css classes.
 * @enum {string}
 */
gc.Notification.CssClass = {
  /** Used for success confirmations. */
  SUCCESS: 'gc-yellow-rnd',

  /** Red display. Used mostly for errors. */
  ERROR: 'gc-red-rnd'
};

/**
 * Time in ms to display the notification for.
 * @enum {number}
 */
gc.Notification.DisplayTime = {
  SUCCESS: 10000,
  ERROR: 8000
};

/**
 * Current CSS class applied to the notification element.
 * @type {string?}
 * @private
 */
gc.Notification.prototype.currentClass_ = null;

/**
 * Reference to the timer instance.
 * @type {goog.Delay}
 * @private
 */
gc.Notification.prototype.timer_ = null;

/**
 * Return a reference to the global instance.
 *
 * @param {Element|string} element The element to use as the notification
 *     area.
 * @return {gc.Notification} An instance of the notification component.
 */
gc.Notification.getInstance = function(element) {
  element = goog.dom.getElement(element);

  if (!element.id) {
    throw Error('gc.Notification areas must have ids');
  }

  if (!gc.Notification.instances_.containsKey(element.id)) {
    gc.Notification.instances_.set(element.id, new gc.Notification(element));
  }

  return /** @type {gc.Notification} */ (
      gc.Notification.instances_.get(element.id));
};

/**
 * Dispose a notification that is no longer needed.
 *
 * @param {gc.Notification} notification The notification element to dispose.
 */
gc.Notification.disposeInstance = function(notification) {
  gc.Notification.instances_.remove(notification.display_.id);
};

/**
 * Display a message in the notification area.
 * Does delay setup if delay time value given.
 *
 * @param {string|Element} message The message to display.
 * @param {?string=} opt_className The class to apply to the area.
 * @param {?number=} opt_timeOut The optional timeout value in ms.
 * @param {?number=} opt_delay Show message after initial delay.
 */
gc.Notification.prototype.show = function(message,
                                          opt_className,
                                          opt_timeOut,
                                          opt_delay) {
  if (opt_delay) {
    this.showDelay_ = new goog.Delay(goog.bind(this.show_, this, message,
        opt_className, opt_timeOut), opt_delay);
    this.showDelay_.start();
  } else {
    this.show_(message, opt_className, opt_timeOut);
  }
};

/**
 * Displays an in-progress notification, such as "Saving...".
 *
 * @param {string|Element} message The message to display.
 */
gc.Notification.prototype.showWorking = function(message) {
  this.show(message, gc.Notification.CssClass.SUCCESS);
};

/**
 * Displays a success notification, such as "Saved.".
 *
 * @param {string|Element} message The message to display.
 */
gc.Notification.prototype.showSuccess = function(message) {
  this.show(message,
            gc.Notification.CssClass.SUCCESS,
            gc.Notification.DisplayTime.SUCCESS);
};

/**
 * Displays an error notification, such as "Error, please try again.".
 *
 * @param {string|Element} message The message to display.
 */
gc.Notification.prototype.showError = function(message) {
  this.show(message,
            gc.Notification.CssClass.ERROR,
            gc.Notification.DisplayTime.ERROR);
};

/**
 * Returns a reference to the display element.
 *
 * @return {Element} The display element.
 */
gc.Notification.prototype.getElement = function() {
  return this.display_;
};

/**
 * Display a message in the notification area.
 * Add class to element, showElement, and adding a reset timer.
 *
 * @param {string|Element} message The message to display.
 * @param {?string=} opt_className The class to apply to the area.
 * @param {?number=} opt_timeOut The optional timeout value in ms.
 * @private
 */
gc.Notification.prototype.show_ = function(message,
                                           opt_className,
                                           opt_timeOut) {
  if (opt_className) {
    if (this.currentClass_) {
      goog.dom.classes.remove(this.display_, this.currentClass_);
    }
    goog.dom.classes.add(this.display_, opt_className);
    this.currentClass_ = opt_className;
  }

  if (goog.isString(message)) {
    this.textElement_.innerHTML = message;
  } else {
    goog.dom.removeChildren(this.textElement_);
    goog.dom.appendChild(this.textElement_, message);
  }
  goog.style.showElement(this.display_, true);

  if (this.timer_) {
    this.timer_.stop();
  }

  if (opt_timeOut) {
    this.timer_ = new goog.Delay(this.reset, opt_timeOut, this);
    this.timer_.start();
  }
};

/**
 * Resets notification area.
 */
gc.Notification.prototype.reset = function() {
  if (this.showDelay_) {
    this.showDelay_.stop();
  }

  if (this.currentClass_) {
    goog.dom.classes.remove(this.display_, this.currentClass_);
    this.currentClass_ = null;
  }

  goog.style.showElement(this.display_, false);
};

