/**
 * Handle the data response that is returned from the background script.
 *
 * @param {Object} response The response object with data about the response.
 * @private
 */
gc.CallWidget.prototype.handleDataResponse_ = function(response) {
  if (response['phones']) {
    this.phones_ = response['phones'];
    this.did_ = response['did'];
    this.rnrSessionId_ = response['r'];

    var phoneIds = {};

    var i = 0;
    goog.object.forEach(this.phones_, function(val, idx, obj) {
      if (val) {
        var option = new goog.ui.Option(
            goog.string.unescapeEntities(val['name']));
        option.setValue(val['phoneNumber']);
        phoneIds[val['phoneNumber']] = 1;
        this.phoneSelect_.addItemAt(option, i++);
      }
    }, this);

    if (response['savedPhone'] && phoneIds[response['savedPhone']]) {
      this.phoneSelect_.setValue(response['savedPhone']);
    } else {
      this.phoneSelect_.setSelectedIndex(0);
    }

    var menu = this.phoneSelect_.getMenu().getElement();
    goog.dom.classes.add(menu, 'gc-cs');
  }
};

/**
 * Send call request.
 *
 * @param {goog.events.Event} e The event from the connect button.
 * @private
 */
gc.CallWidget.prototype.handleCall_ = function(e) {
  var phone = this.phoneSelect_.getValue();

  if (this.isCalling_) {
    this.resetButtons_();

    chrome.extension.sendMessage({
      'action': 'cancel',
      'number': this.number_,
      'phone': phone
    }, function() {});
  } else {
    this.connect_.setEnabled(false);
    this.connect_.setCaption(gc.MSG_CONNECTING);

    chrome.extension.sendMessage({
      'action': 'call',
      'number': this.number_,
      'phone': phone
    }, goog.bind(this.callResponse_, this));
  }
};

/**
 * Response to the make call request.  In case the call cannot be made because
 * of an outgoing number that matches the user's DID or one of their forwarding
 * phones, we display the error text returned from the server.
 *
 * @param {Object} response The response object.
 * @private
 */
gc.CallWidget.prototype.callResponse_ = function(response) {
  this.connect_.setEnabled(true);
  this.connect_.setCaption(gc.MSG_CONNECT);

  var success = false;

  if (response['responseText']) {
    var json = {};
    try {
      json = JSON.parse(response['responseText']);
    } catch (err) {
      json = {'ok': false};
    }

    if (json['ok']) {
      success = true;
      this.retry_ = true;

      // if response is okay, display call status information.
      this.connect_.setCaption('Hang up');
      this.isCalling_ = true;
      this.callMsg_.reset();

      this.callMsg_.show(gc.MSG_CALLING_YOU, 'gc-quickcall-msg',
          gc.CallWidget.MESSAGE_DELAY);

      // Reset everything after a long delay.
      var timer = new goog.Delay(goog.bind(this.resetButtons_, this),
          gc.CallWidget.RESET_DELAY, this);
      timer.start();
    }
  }

  if (!success) {
    // Will only retry once.
    if (this.retry_) {
      this.retry_ = false;
      this.isCalling_ = false;
      this.handleCall_();
    } else {
      var errorMessage = gc.MSG_QUICK_ACTION_ERROR;
      this.isCallDisabled_ = false;

      // 20 is Invalid Number.
      if (response['data'] && response['data']['code'] == '20') {
        errorMessage = gc.MSG_INVALID_NUMBER_SHORT;
      }

      this.callMsg_.show(errorMessage, 'gc-quickcall-err');
    }
  }
};

/**
 * Resets the 'Cancel' and 'Connect' buttons, plus their status messages
 * back to original state.
 *
 * @private
 */
gc.CallWidget.prototype.resetButtons_ = function() {
  this.callMsg_.reset();
  this.connect_.setEnabled(true);
  this.connect_.setCaption(gc.MSG_CONNECT);
  this.connect_.setActive(false);
  this.isCalling_ = false;
};

/**
 * Sets whether to disable or enable the auto hiding by adjusting the region.
 *
 * NOTE(brianp): We set the auto hide region instead of calling popup's
 * setAutoHide method. The reason for this is so that you can set the auto
 * hide state while the popup is open. This is needed when there are popups
 * inside the main popup and we don't want to hide the
 * popup when someone clicks on one of the popups/menus inside the main one.
 *
 * @param {boolean} autoHide Whether to hide if user clicks outside the popup.
 */
gc.CallWidget.prototype.setAutoHideRegion = function(autoHide) {
  if (autoHide) {
    this.popup_.setAutoHideRegion(null);
  } else {
    this.popup_.setAutoHideRegion(goog.dom.getDocument().body.firstChild);
  }
};

/**
 * Decorate the given div element as a Closure CustomButton.
 *
 * @param {Element} divElement The div element to decorate.
 * @param {goog.ui.ControlRenderer?} opt_renderer Optional renderer to use.
 * @return {goog.ui.CustomButton} The custom button object.
 */
gc.CallWidget.decorateButton = function(divElement, opt_renderer) {
  var button = new goog.ui.CustomButton(null,
      opt_renderer || goog.ui.style.app.ButtonRenderer.getInstance());
  button.decorate(divElement);
  return button;
};




